let time_amount = case(
        '{time_unit}' == 'Minutes', timespan('{time_quantity}m'),
        '{time_unit}' == 'Hours', timespan('{time_quantity}h'),
        '{time_unit}' == 'Days', timespan('{time_quantity}d'),
        timespan('24h')
);
let recentData = GuardicoreAgents_CL
| where TimeGenerated >= ago(7d);

let max_ts = toscalar(
    recentData
    | summarize max(sampling_timestamp_d)
);
recentData
| where sampling_timestamp_d == max_ts
| where unixtime_milliseconds_todatetime(last_seen_d) < ago(time_amount)
| project 
    AgentName = display_name_s, 
    AgentID=asset_id_g, 
    LastSeen = unixtime_milliseconds_todatetime(last_seen_d)

let time_amount = case(
        '{time_unit}' == 'Minutes', timespan('{time_quantity}m'),
        '{time_unit}' == 'Hours', timespan('{time_quantity}h'),
        '{time_unit}' == 'Days', timespan('{time_quantity}d'),
        timespan('24h')
);

let startDate = endofday(ago(7d));
let endDate = endofday(now());
let all_days = range day from startDate to endDate step 1d;

let rawData = GuardicoreAgents_CL
| where TimeGenerated >= startDate
| extend sampling_ts = unixtime_seconds_todatetime(sampling_timestamp_d)
| extend day = endofday(sampling_ts);

let daily_max_ts = rawData
| summarize max_ts = max(sampling_ts) by day;

let daily_missing_counts = rawData
| join kind=inner (
    daily_max_ts
) on day, $left.sampling_ts == $right.max_ts
| where unixtime_milliseconds_todatetime(last_seen_d) < (day - time_amount)
| summarize missing_agents = count() by day;

all_days
| join kind=leftouter (daily_missing_counts) on day
| extend missing_agents = coalesce(missing_agents, 0)
| sort by day asc
| where day > startDate and day != endDate
| project day = format_datetime(day, 'yyyy-MM-dd'), missing_agents

let startDate = endofday(ago(7d));
let endDate = endofday(now());
let all_days = range day from startDate to endDate step 1d;

let rawData = GuardicorePolicyRules_CL
| where TimeGenerated >= startDate
| extend sampling_ts = unixtime_seconds_todatetime(sampling_timestamp_d)
| extend day = endofday(sampling_ts);

let daily_max_ts = rawData
| summarize max_ts = max(sampling_ts) by day;

let daily_counts = rawData
| join kind=inner (
    daily_max_ts
) on day, $left.sampling_ts == $right.max_ts
| summarize value = count() by day;

all_days
| join kind=leftouter (daily_counts) on day
| extend value = coalesce(value, 0)
| sort by day asc
| where day > startDate and day != endDate
| project day = format_datetime(day, 'yyyy-MM-dd'), value

let recentData = GuardicorePolicyRules_CL
| where TimeGenerated >= ago(24h);

let max_ts = toscalar(
    recentData
    | summarize max(sampling_timestamp_d)
);

recentData
| where sampling_timestamp_d == max_ts
| summarize count = count() by action_s
| extend actionFormatted = replace("_", " ", tolower(action_s))
| extend actionFormatted = strcat(toupper(substring(actionFormatted, 0, 1)), substring(actionFormatted, 1))
| project Action = actionFormatted, Count = count
| order by Count desc
